function jsonDataLoad($fileName)
{
    $jsonDataLayout = __DIR__ . '/sections/' . $fileName ;

    if(file_exists($jsonDataLayout))
    {
        $templateDataArray = file_get_contents($jsonDataLayout);

        $templateDataArray = json_decode($templateDataArray);

        $templateArray = [];

        if($templateDataArray != FALSE)
        {
            return array(
                "class"     => $templateDataArray->class,
                "data"      => json_decode($templateDataArray->data, true),
                "media"     => json_decode($templateDataArray->media, true),
                "meta"      => json_decode($templateDataArray->meta, true),
                "version"   => $templateDataArray->editorVersion,
                "files"     => $templateDataArray->files,
                "hasPro"    => $templateDataArray->hasPro,
            );
        }

        return $templateArray;
    }
    else
    {
        return false;
    }
}